package com.example.springbootalipaydemo.alipay;

/**
 *类名：AlipayConfig
 *功能：基础配置类
 *详细：设置帐户有关信息及返回路径
 *说明：宝接口使用，只是提供一个参考。
 */
public class AlipayConfig {

    // 应用ID,您的APPID，收款账号既是您的APPID对应支付宝账号
    //  public static String app_id = "2016092000552215";
    public static String app_id = "2021000117641174";

    // 商户私钥，您的PKCS8格式RSA2私钥
    // public static String merchant_private_key = "";
    public static String merchant_private_key = "";


    // 支付宝公钥,查看地址：https://openhome.alipay.com/platform/keyManage.htm 对应APPID下的支付宝公钥。
    //public static String alipay_public_key = "";
    public static String alipay_public_key = "";

    // 服务器异步通知页面路径  需http://格式的完整路径，不能加?id=123这类自定义参数，必须外网可以正常访问
    public static String notify_url = "http://172.8.7.208:9888/notify_url.jsp";

    // 页面跳转同步通知页面路径 需http://格式的完整路径，不能加?id=123这类自定义参数，必须外网可以正常访问
    public static String return_url = "http://172.8.7.208:9888/payResult/return_url";

    // 签名方式
    public static String sign_type = "RSA2";

    // 字符编码格式
    public static String charset = "utf-8";

    // 支付宝网关 //
    //public static String gatewayUrl = "https://openapi.alipay.com/gateway.do";// 正式
    public static String gatewayUrl = "https://openapi.alipaydev.com/gateway.do";// 测试

}


